+      >
+        <ul style={{ margin: 0, display: 'flex' }}>{dots}</ul>
+      </div>
+    ),
+    customPaging: () => (
+      <div
+        style={{
+          width: '30px',
+          height: '4px',
+          background: '#fff',
+          margin: '5px 4px',
+          borderRadius: '2px',
+          cursor: 'pointer',
+        }}
+      />
+    ),
+  };
+
+  // Slides data
+  const slides = [
+    {
+      id: 1,
+      bgImage: bgImage,
+      heading: 'Get Ready for New Adidas Brands',
+      subtext: `Adidas track and field is coming with a riveting gear 
+                and a fresh look. Get everything to enhance your track performance.`,
